#Step 1: create DEV Hash$AnalyzeLanguages = 'NLB','ENU'$DEVLang = 'ENU'$DEVHash =     Export-NAVApplicationObjectLanguageHash `        -Source C:\Workingfolder\TranslatePSC\PSC.txt `        -DestinationXML C:\Workingfolder\TranslatePSC\\DevHash.xml `        -LanguageId $AnalyzeLanguages `        -DevelopmentLanguageId $DEVLang `        -WorkingFolder C:\Workingfolder\TranslatePSC\  $ObjectLanguages =     Get-NAVApplicationObjectLanguage `        -Source C:\Workingfolder\TranslatePSC\PSC.txt `        -WorkingFolder C:\Workingfolder\TranslatePSC\ `        -LanguageId $AnalyzeLanguages `        -DevelopmentTranslations $DEVHash `        -NotOnlyMissingTranslationscls$ObjectLanguages | Export-Csv C:\Workingfolder\TranslatePSC\Translation.csvbreakImport-NAVApplicationObjectLanguage -Source C:\Workingfolder\TranslatePSC\PSC.txt -LanguagePath C:\Workingfolder\TranslatePSC\FinalTranslation.txt -Destination C:\Workingfolder\TranslatePSC\PSC_Translated.txt -Force 